        //     setPosts(posts => setPosts([...posts, ...json]));
        //     setLoad(false);
        // }
    }
    if(inView) loadData();

    return(
    <Container>
        <Grid spacing={3} container>
          {likedNews.map(post => (
            <Grid item xs={12} md={3} sm={6} key={post.url}>
              <NewsCard post={post}/>
            </Grid>
          ))}
        </Grid>
        <div ref={ref} style={{display: "flex", justifyContent: "center", paddingTop:"15px"}}>
          { load ? <CircularProgress /> : "" }
        </div>
    </Container>
    );
}
export default LikedNews;
